mapper.select:
select distinct ACCTNUM from GLSUM  WHERE ENTITYID = #{entityId} AND PERIOD = #{period} and BASIS = #{basis} and acctnum like 'CI%' and REPLACE(acctnum,'CI','') between #{beginAcctRange} and #{endAcctRange}
UNION
select distinct ACCTNUM  from BUDGETS WHERE ENTITYID = #{entityId} AND PERIOD = #{period} and BASIS = #{basis} and acctnum like 'CI%' and REPLACE(acctnum,'CI','') between #{beginAcctRange} and #{endAcctRange}
